function uber_op( id , args , def ){

	//If no id
	if( !ubermenu_data.hasOwnProperty( id ) ){
		return def;
	}

	var val = ubermenu_data[id];

	if( args.hasOwnProperty( 'datatype' ) ){
		switch( args['datatype'] ){
			case 'numeric':
				val = parseInt( val );
				break;
			
			case 'boolean':
				if( val == 'on' || val == 1 || val == '1' ){
					val = true;
				}
				else{
					val = false;
				}
				break;
		}
	}

	return val;
}



